    if (forward) {
      this.selectedProductForm.get('currentImageIndex').setValue(nextIndex);
    } // If cycling backwards...
    else {
      this.selectedProductForm.get('currentImageIndex').setValue(prevIndex);
    }
  }
  /**
   * Toggle the tags edit mode
   */


  toggleTagsEditMode() {
    this.tagsEditMode = !this.tagsEditMode;
  }
  /**
   * Filter tags
   *
   * @param event
   */


  filterTags(event) {
    // Get the value
    const value = event.target.value.toLowerCase(); // Filter the tags

    this.filteredTags = this.tags.filter(tag => tag.title.toLowerCase().includes(value));
  }
  /**
   * Filter tags input key down event
   *
   * @param event
   */


  filterTagsInputKeyDown(event) {
    // Return if the pressed key is not 'Enter'
    if (event.key !== 'Enter') {
      return;
    } // If there is no tag available...


    if (this.filteredTags.length === 0) {
      // Create the tag
      this.createTag(event.target.value); // Clear the input

      event.target.value = ''; // Return

      return;
    } // If there is a tag...


    const tag = this.filteredTags[0];
    const isTagApplied = this.selectedProduct.tags.find(id => id === tag.id); // If the found tag is already applied to the product...

    if (isTagApplied) {
      // Remove the tag from the product
      this.removeTagFromProduct(tag);
    } else {
      // Otherwise add the tag to the product
      this.addTagToProduct(tag);
    }
  }
  /**
   * Create a new tag
   *
   * @param title
   */


  createTag(title) {
    const tag = {
      title
    }; // Create tag on the server

    this._inventoryService.createTag(tag).subscribe(response => {
      // Add the tag to the product
      this.addTagToProduct(response);
    });
  }
  /**
   * Update the tag title
   *
   * @param tag
   * @param event
   */


  updateTagTitle(tag, event) {
    // Update the title on the tag
    tag.title = event.target.value; // Update the tag on the server

    this._inventoryService.updateTag(tag.id, tag).pipe(debounceTime(300)).subscribe(); // Mark for check


    this._changeDetectorRef.markForCheck();
  }
  /**
   * Delete the tag
   *
   * @param tag
   */


  deleteTag(tag) {
    // Delete the tag from the server
    this._inventoryService.deleteTag(tag.id).subscribe(); // Mark for check


    this._changeDetectorRef.markForCheck();
  }
  /**
   * Add tag to the product
   *
   * @param tag
   */


  addTagToProduct(tag) {
    // Add the tag
    this.selectedProduct.tags.unshift(tag.id); // Update the selected product form

    this.selectedProductForm.get('tags').patchValue(this.selectedProduct.tags); // Mark for check

    this._changeDetectorRef.markForCheck();
  }
  /**
   * Remove tag from the product
   *
   * @param tag
   */


  removeTagFromProduct(tag) {
    // Remove the tag
    this.selectedProduct.tags.splice(this.selectedProduct.tags.findIndex(item => item === tag.id), 1); // Update the selected product form

    this.selectedProductForm.get('tags').patchValue(this.selectedProduct.tags); // Mark for check

    this._changeDetectorRef.markForCheck();
  }
  /**
   * Toggle product tag
   *
   * @param tag
   * @param change
   */


  toggleProductTag(tag, change) {
    if (change.checked) {
      this.addTagToProduct(tag);
    } else {
      this.removeTagFromProduct(tag);
    }
  }
  /**
   * Should the create tag button be visible
   *
   * @param inputValue
   */


  shouldShowCreateTagButton(inputValue) {
    return !!!(inputValue === '' || this.tags.findIndex(tag => tag.title.toLowerCase() === inputValue.toLowerCase()) > -1);
  }
  /**
   * Create product
   */


  createProduct() {
    // Create the product
    this._inventoryService.createProduct().subscribe(newProduct => {
      // Go to new product
      this.selectedProduct = newProduct; // Fill the form

      this.selectedProductForm.patchValue(newProduct); // Mark for check

      this._changeDetectorRef.markForCheck();
    });
  }
  /**
   * Update the selected product using the form data
   */


  updateSelectedProduct() {
    // Get the product object
    const product = this.selectedProductForm.getRawValue(); // Remove the currentImageIndex field

    delete product.currentImageIndex; // Update the product on the server

    this._inventoryService.updateProduct(product.id, product).subscribe(() => {
      // Show a success message
      this.showFlashMessage('success');
    });
  }
  /**
   * Delete the selected product using the form data
   */


  deleteSelectedProduct() {
    // Open the confirmation dialog
    const confirmation = this._fuseConfirmationService.open({
      title: 'Delete product',
      message: 'Are you sure you want to remove this product? This action cannot be undone!',
      actions: {
        confirm: {
          label: 'Delete'
        }
      }
    }); // Subscribe to the confirmation dialog closed action


    confirmation.afterClosed().subscribe(result => {
      // If the confirm button pressed...
      if (result === 'confirmed') {
        // Get the product object
        const product = this.selectedProductForm.getRawValue(); // Delete the product on the server

        this._inventoryService.deleteProduct(product.id).subscribe(() => {
          // Close the details
          this.closeDetails();
        });
      }
    });
  }
  /**
   * Show flash message
   */


  showFlashMessage(type) {
    // Show the message
    this.flashMessage = type; // Mark for check

    this._changeDetectorRef.markForCheck(); // Hide it after 3 seconds


    setTimeout(() => {
      this.flashMessage = null; // Mark for check

      this._changeDetectorRef.markForCheck();
    }, 3000);
  }
  /**
   * Track by function for ngFor loops
   *
   * @param index
   * @param item
   */


  trackByFn(index, item) {
    return item.id || index;
  }

}

